INSERT INTO pin (id, creatorID, latitude, longitude, title, address, description, image, likes) VALUES
(300, 46, 40.169328, -124.2271324, 'Lost Coast Trail', '3750 Lighthouse Rd, Petrolia, CA 95558, United States', 'Backpack California’s remote Lost Coast from the Mattole trailhead—wild beaches, tide-dependent headlands, and King Range scenery.', 'https://commons.wikimedia.org/wiki/Special:FilePath/The%20Lost%20Coast%20Trail%2001.jpg?width=1600', 75),
(301, 12, 34.1708578, -117.9332738, 'Fern Canyon', 'Gold Bluffs Beach Rd, Orick, CA 95555, United States', 'Walk through a lush, fern-draped slot canyon near Gold Bluffs Beach in Prairie Creek Redwoods State Park—expect creek crossings in-season.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Fern%20canyon%207x10.jpg?width=1600', 91),
(302, 37, 41.17556, -122.33944, 'Castle Crags Dome Trail', '20022 Castle Creek Rd, Castella, CA 96017, United States', 'Steep, rewarding hike to Castle Dome viewpoints in Castle Crags State Park, with big granite formations and Mount Shasta vistas.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Castle%20Dome%20and%20Mount%20Shasta.jpg?width=1600', 24),
(303, 43, 40.4532818, -121.4940683, 'Bumpass Hell Trail', 'Lassen Volcanic National Park Hwy, Mineral, CA 96063, United States', 'Classic Lassen hike to the park’s largest hydrothermal basin—boiling pools, fumaroles, and boardwalk overlooks (seasonal access varies).', 'https://commons.wikimedia.org/wiki/Special:FilePath/Bumpass%20Hell%2C%20Lassen%20Volcanic%20National%20Park%2C%20California%20(23237985341).jpg?width=1600', 59),
(304, 14, 39.6261391, -121.2683679, 'Feather Falls Trail', 'Roseben Ave, Oroville, CA 95966, United States', 'Forest hike to one of California’s tallest waterfalls—Feather Falls plunges dramatically into a rugged canyon (hot in summer).', 'https://commons.wikimedia.org/wiki/Special:FilePath/Feather%20falls%20plumas%20national%20forest.jpg?width=1600', 32),
(305, 18, 37.9537367, -122.7833527, 'Alamere Falls', 'Mesa Rd, Bolinas, CA 94924, United States', 'Iconic Point Reyes trek from the Palomarin trailhead to a rare tidefall where Alamere Creek drops onto the beach (stay on official routes).', 'https://commons.wikimedia.org/wiki/Special:FilePath/Top%20of%20Alamere%20Falls.jpg?width=1600', 93),
(306, 26, 37.93184, -122.63539, 'Cataract Falls Trail', 'Bolinas-Fairfax Rd, Fairfax, CA 94930, United States', 'Marin County favorite featuring a series of cascades on Cataract Creek—best after winter rains and during spring flow.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Cataract%20Falls%2C%20Mount%20Tamalpais%20Watershed.jpg?width=1600', 86),
(307, 21, 37.429725, -122.3371513, 'Purisima Creek Redwoods', '13130 Skyline Blvd, Woodside, CA 94062, United States', 'Redwood canyon hiking in the Santa Cruz Mountains with ferny creek corridors and occasional coastal views from ridgeline trails.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Purisima%20Creek%20vista.jpg?width=1600', 18),
(308, 23, 36.0873537, -121.5946174, 'Boronda Trail', 'CA-1, Big Sur, CA 93920, United States', 'Very steep Big Sur climb into the Ventana Wilderness via Boronda Ridge, rewarding hikers with dramatic coastal and Santa Lucia Range views.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Ventana%20wilderness.jpg?width=1600', 15),
(309, 26, 35.81542, -121.35915, 'Salmon Creek Falls Trail', 'CA-1, Big Sur, CA 93920, United States', 'A short, sweet hike off Highway 1 to a mossy coastal waterfall in the Silver Peak Wilderness area.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Salmon%20Creek%20(Big%20Sur)%20-%20panoramio.jpg', 50),
(310, 36, 35.2728, -120.8887, 'Montaña de Oro Bluff Trail', '3550 Pecho Valley Rd, Los Osos, CA 93402, United States', 'Coastal bluff-top walking with nonstop ocean views, rugged cliffs, and access to beaches and tidepools.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Montana%20de%20Oro%20State%20Park%20-%20panoramio%20(1).jpg', 49),
(311, 15, 34.28306, -117.74667, 'Bridge to Nowhere Hike', 'East Fork Rd, Azusa, CA 91702, United States', 'A classic river hike with multiple stream crossings that ends at the famous “Bridge to Nowhere” in the San Gabriel Mountains.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Bridge%20to%20Nowhere%20-%20San%20Gabriel%20River.jpg', 100),
(312, 16, 33.7034924, -116.2269696, 'Painted Canyon', 'Painted Canyon Rd, Mecca, CA 92254, United States', 'A colorful desert slot-and-ladder style canyon route with sculpted walls and an adventurous feel near Mecca.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Painted%20Canyon%20Mecca%20California.jpg', 17),
(313, 24, 33.76609, -117.67915, 'Black Star Canyon Trail', '13333 Black Star Canyon Rd, Silverado, CA 92676, United States', 'A stream-crossing canyon hike in the Santa Ana Mountains that leads toward seasonal falls and shaded riparian scenery.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Black%20Star%20Canyon%20-%20panoramio.jpg', 96),
(314, 20, 33.1824871, -116.2154463, 'The Slot', 'Borrego Mountain Wash, Borrego Springs, CA 92004, United States', 'A fun, family-friendly slot canyon adventure with tight walls and sandy washes in Anza-Borrego Desert State Park.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Anza-Borrego%20Desert%20State%20Park%20Slot%20Canyon.jpg', 0),
(315, 31, 37.13215, -118.50927, 'Big Pine Lakes Trail', 'Glacier Lodge Rd, Big Pine, CA 93513, United States', 'A stunning alpine trek to a chain of turquoise lakes beneath the Palisades, with classic Eastern Sierra views.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Big%20Pine%20Lakes%20-%20John%20Muir%20Wilderness.jpg', 28),
(316, 35, 37.9295411, -119.045671, 'Panum Crater', '1 Visitor Center Dr, Lee Vining, CA 93541, United States', 'An easy volcanic loop that lets you walk the rim and into the crater with wide-open views of Mono Lake and the Sierra.', 'https://commons.wikimedia.org/wiki/Special:FilePath/Panum%20Crater%20Mono%20Basin.jpg', 97),
(317, 45, 34.4398008, -119.0805579, 'Santa Paula Canyon', '17001 Santa Paula St, Santa Paula, CA 93060, United States', 'A creekside canyon hike to the famous Punch Bowls—clear pools and cascades perfect for a warm-day adventure.', 'https://ropewiki.com/images/0/03/Santa_Paula_Creek_pic1.jpg', 93),
(318, 39, 32.9899492, -116.7296427, 'Cedar Creek Falls', '15519 Thornbush Rd, Ramona, CA 92065, United States', 'A permit-managed San Diego classic featuring a rugged gorge hike to a tall waterfall and swimming hole (seasonal).', 'https://commons.wikimedia.org/wiki/Special:FilePath/Cleveland%20National%20Forest%20-%20Social%206.jpg', 23),
